export default(a,r)=>{const t=document.querySelector(a),e=document.querySelector(r);t.addEventListener("change",function(){const o=new URL(window.location.href);o.searchParams.set("tenant",t.value),e.disabled=!0,fetch(o).then(n=>n.text()).then(n=>{const s=new DOMParser().parseFromString(n,"text/html").querySelector(r);e.innerHTML=s.innerHTML||"",e.options[0].dataset.value=t.options[t.selectedIndex].dataset.value,e.disabled=!1,e.dispatchEvent(new Event("change"))})})};
